import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const BookUpload = () => {
  const [file, setFile] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    category: '',
    description: ''
  });
  const [dragActive, setDragActive] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [message, setMessage] = useState({ type: '', text: '' });
  const navigate = useNavigate();

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      handleFileValidation(e.dataTransfer.files[0]);
    }
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      handleFileValidation(e.target.files[0]);
    }
  };

  const handleFileValidation = (selectedFile) => {
    if (selectedFile.type !== 'application/pdf') {
      setMessage({ type: 'error', text: 'Only PDF files are allowed!' });
      setFile(null);
      return;
    }
    if (selectedFile.size > 10 * 1024 * 1024) {
      setMessage({ type: 'error', text: 'File size must be less than 10MB!' });
      setFile(null);
      return;
    }
    setFile(selectedFile);
    setShowForm(true);
    setMessage({ type: '', text: '' });
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setMessage({ type: 'error', text: 'Please select a PDF file!' });
      return;
    }

    const data = new FormData();
    data.append('bookFile', file);
    data.append('title', formData.title);
    data.append('author', formData.author);
    data.append('category', formData.category);
    data.append('description', formData.description);

    try {
      setUploading(true);
      setUploadProgress(0);

      await axios.post('http://localhost:8082/api/books/upload', data, {
        headers: { 'Content-Type': 'multipart/form-data' },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setUploadProgress(progress);
        }
      });

      setMessage({ type: 'success', text: 'Book uploaded successfully!' });
      setTimeout(() => navigate('/books'), 2000);
    } catch (error) {
      setMessage({ 
        type: 'error', 
        text: error.response?.data?.message || 'Upload failed. Please try again.' 
      });
    } finally {
      setUploading(false);
    }
  };

  const removeFile = () => {
    setFile(null);
    setShowForm(false);
    setFormData({ title: '', author: '', category: '', description: '' });
    setMessage({ type: '', text: '' });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-100 via-purple-50 to-pink-100 py-12 px-4">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-3xl shadow-2xl overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-purple-600 to-indigo-600 px-8 py-6">
            <h2 className="text-3xl font-bold text-white text-center">üìö Upload New Book</h2>
            <p className="text-purple-100 text-center mt-2">Share your knowledge with the world</p>
          </div>

          <form onSubmit={handleSubmit} className="p-8 space-y-8">
            {/* Drag and Drop Area */}
            <div
              className={`relative border-4 border-dashed rounded-2xl transition-all duration-300 ease-in-out ${
                dragActive 
                  ? 'border-purple-500 bg-purple-50 scale-[1.02] shadow-2xl' 
                  : file
                  ? 'border-green-400 bg-green-50'
                  : 'border-gray-300 bg-gray-50 hover:border-purple-400 hover:bg-purple-50/30'
              }`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <input
                type="file"
                accept=".pdf"
                onChange={handleFileChange}
                className="hidden"
                id="fileInput"
              />

              <div className="p-12">
                {!file ? (
                  <div className="text-center space-y-6">
                    {/* Animated Icon */}
                    <div className={`text-8xl transition-transform duration-300 ${
                      dragActive ? 'scale-110 animate-bounce' : 'scale-100'
                    }`}>
                      {dragActive ? 'üì•' : 'üìÑ'}
                    </div>

                    <div>
                      <h3 className="text-2xl font-bold text-gray-700 mb-2">
                        {dragActive ? 'Drop it here!' : 'Drag & Drop your PDF'}
                      </h3>
                      <p className="text-gray-500 mb-4">or</p>
                      <label
                        htmlFor="fileInput"
                        className="inline-block px-8 py-4 bg-gradient-to-r from-purple-600 to-indigo-600 text-white text-lg font-semibold rounded-xl cursor-pointer hover:from-purple-700 hover:to-indigo-700 transition-all duration-300 transform hover:scale-105 hover:shadow-xl"
                      >
                        Browse Files
                      </label>
                    </div>

                    <div className="flex items-center justify-center space-x-4 text-sm text-gray-400">
                      <span className="flex items-center">
                        <svg className="w-5 h-5 mr-1" fill="currentColor" viewBox="0 0 20 20">
                          <path d="M9 2a2 2 0 00-2 2v8a2 2 0 002 2h6a2 2 0 002-2V6.414A2 2 0 0016.414 5L14 2.586A2 2 0 0012.586 2H9z"/>
                        </svg>
                        PDF Only
                      </span>
                      <span>‚Ä¢</span>
                      <span className="flex items-center">
                        <svg className="w-5 h-5 mr-1" fill="currentColor" viewBox="0 0 20 20">
                          <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd"/>
                        </svg>
                        Max 10MB
                      </span>
                    </div>
                  </div>
                ) : (
                  <div className="text-center space-y-4">
                    <div className="text-7xl animate-bounce">‚úÖ</div>
                    <div className="bg-white rounded-xl p-6 shadow-lg">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                          <div className="text-5xl">üìï</div>
                          <div className="text-left">
                            <p className="font-semibold text-gray-800 text-lg">{file.name}</p>
                            <p className="text-sm text-gray-500">{(file.size / 1024 / 1024).toFixed(2)} MB</p>
                          </div>
                        </div>
                        <button
                          type="button"
                          onClick={removeFile}
                          className="text-red-500 hover:text-red-700 hover:bg-red-50 p-2 rounded-full transition-all"
                        >
                          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"/>
                          </svg>
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Form Fields */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">üìñ Book Title *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all"
                  placeholder="Enter book title"
                />
              </div>

              <div>
                <label className="block text-sm font-bold text-gray-700 mb-2">‚úçÔ∏è Author *</label>
                <input
                  type="text"
                  name="author"
                  value={formData.author}
                  onChange={handleInputChange}
                  required
                  className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all"
                  placeholder="Enter author name"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">üè∑Ô∏è Category *</label>
              <input
                type="text"
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                required
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all"
                placeholder="e.g., Fiction, Science, History"
              />
            </div>

            <div>
              <label className="block text-sm font-bold text-gray-700 mb-2">üìù Description *</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
                rows="4"
                className="w-full px-4 py-3 border-2 border-gray-300 rounded-xl focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition-all resize-none"
                placeholder="Enter book description"
              />
            </div>

            {/* Progress Bar */}
            {uploading && (
              <div className="space-y-2">
                <div className="flex justify-between text-sm font-semibold text-gray-700">
                  <span>Uploading...</span>
                  <span>{uploadProgress}%</span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-4 overflow-hidden shadow-inner">
                  <div
                    className="bg-gradient-to-r from-purple-500 via-pink-500 to-indigo-500 h-full transition-all duration-300 rounded-full flex items-center justify-end pr-2"
                    style={{ width: `${uploadProgress}%` }}
                  >
                    {uploadProgress > 10 && <span className="text-white text-xs font-bold">üì§</span>}
                  </div>
                </div>
              </div>
            )}

            {/* Messages */}
            {message.text && (
              <div
                className={`p-4 rounded-xl font-semibold flex items-center space-x-2 animate-pulse ${
                  message.type === 'success'
                    ? 'bg-green-100 text-green-700 border-2 border-green-300'
                    : 'bg-red-100 text-red-700 border-2 border-red-300'
                }`}
              >
                <span className="text-2xl">{message.type === 'success' ? '‚úÖ' : '‚ùå'}</span>
                <span>{message.text}</span>
              </div>
            )}

            {/* Submit Button */}
            <button
              type="submit"
              disabled={uploading}
              className={`w-full py-4 rounded-xl font-bold text-white text-lg transition-all duration-300 transform ${
                uploading
                  ? 'bg-gray-400 cursor-not-allowed'
                  : 'bg-gradient-to-r from-purple-600 to-indigo-600 hover:from-purple-700 hover:to-indigo-700 hover:scale-[1.02] shadow-lg hover:shadow-2xl'
              }`}
            >
              {uploading ? '‚è≥ Uploading...' : 'üöÄ Upload Book'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BookUpload;
